select c.id as card_id, c.type as template_type, c.*, t.code as template_code, t.id as template_id, t.name as templateName, sia.is_ecard as is_ecard, sia.settlement_org_id as settlement_org_id, sia.settlement_org_name as settlement_org_name from card c left join template t on c.template_id = t.id inner join stock_in_apply_detail siad on c.id = siad.card_id left join stock_in_apply sia on siad.apply_id = sia.id WHERE c.platform = 'BB' ORDER BY updated_time DESC LIMIT 0,20

select c.id as card_id, c.type as template_type, c.card_no,        c.activation_period_type, c.activation_period, c.activation_unit, c.activation_start_date, c.activation_start_date,        c.service_period_type, c.service_period, c.service_unit, c.service_start_date, c.service_end_date,        t.code as template_code, t.id as template_id, c.name as templateName, sia.is_ecard, sia.settlement_org_id, sia.settlement_org_name     from card c         inner join (select id, code from template) t on c.template_id = t.id         inner join (select card_id, apply_id from stock_in_apply_detail) siad on c.id = siad.card_id         left join (select id, is_ecard, settlement_org_id, settlement_org_name from stock_in_apply) sia on siad.apply_id = sia.idWHERE c.platform = ? ORDER BY updated_time DESC